//node.warn(msg);

const chatId = msg.payload.chatId;
const messageId = msg.payload.messageId;
const callbackData = msg.originalMessage.data;

if (!callbackData) { return null };

let currentExpression = context.get("expression") || "";

if (callbackData === "=") {
    if (!currentExpression) {
        msg.payload = {
            chatId: chatId,
            type: "message",
            content: "Помилка виразу. Спробуйте ще раз.",
        };
        return msg;
    }

    try {
        const result = eval(currentExpression);
        msg.payload = {
            chatId: chatId,
            type: "message",
            content: `Тримай результат: ${result}`,
        };
    } catch (error) {
        msg.payload = {
            chatId: chatId,
            type: "message",
            content: "Щось ти не правильно ввів, спробуй ще раз!",
        };
    }

    context.set("expression", "");
} else {
    currentExpression += callbackData;
    context.set("expression", currentExpression);

    msg.payload = {
        chatId: chatId,
        type: "message",
        content: `Поточний вираз: ${currentExpression}`,
    };
}

return msg;

if (msg.payload.content.startsWith("/start")) { return null };

function calculateExpression(expression) {
    try {
        const result = eval(expression);
        if (isNaN(result) || !isFinite(result)) {
            throw new Error("Invalid expression");
        }
        return result;
    } catch (error) {
        console.error("Error calculating:", error.message);
        return null;
    }
};

let currentExpression = context.get("expression") || "";
const userInput = msg.payload.content;
const mathComponents = userInput.match(/[0-9+\-*/.]/g);
const mathExpression = mathComponents ? mathComponents.join('') : '';
const result = calculateExpression(mathExpression);

msg.payload = {
    chatId: msg.payload.chatId,
    type: "message",
    content: `${result !== null ? result : 'Недійсний вираз, ану поправ'}`,
};

return msg;
