        let buttons = favoriteMovies.map((value) => [{ text: value.title, callback_data: value.title }]);
        options = {
            reply_markup: {
                inline_keyboard: buttons,
            },
        };
    } else {
        content = "Ви не додали жодного фільму до улюблених!";
    }

    msg.payload = {
        chatId: chatId,
        type: "message",
        content: content,
        options: options,
    };
}

